import React, { useReducer } from "react";
import { v4 as uuidv4 } from "uuid";
import ContactContext from "./contactContext";
import contactReducer from "./contactReducer";
import {
  ADD_CONTACT,
  // DELETE_CONTACT,
  // SET_CURRENT,
  // CLEAR_CURRENT,
  // UPDATE_CONTACT,
  // FILTER_CONTACTS,
  // CLEAR_FILTER,
} from "../types";

const ContactState = (props) => {
  const initialState = {
    contacts: [
      {
        id: 1,
        name: "Alessandra Ambrosio",
        email: "aa@gmail.com",
        phone: "415-555-1111",
        type: "professional",
      },
      {
        id: 4,
        name: "Ming Xi",
        email: "xi@mail.com",
        phone: "650-555-4444",
        type: "professional",
      },
      {
        id: 3,
        name: "Laetitia Casta",
        email: "casta@yahoo.com",
        phone: "925-555-3333",
        type: "personal",
      },

      {
        id: 2,
        name: "Lais Ribeiro",
        email: "lais@proton.com",
        phone: "510-555-2222",
        type: "professional",
      },
    ],
  };

  const [state, dispatch] = useReducer(contactReducer, initialState);

  // Actions=====
  // Add Contact
  const addContact = (contact) => {
    contact.id = uuidv4();
    dispatch({ type: ADD_CONTACT, payload: contact });
  };
  // Delete Contact
  // Set Current Contact
  // Clear Current Contact
  // Update Contact
  // Filter Contact
  // Clear Filter

  return (
    <ContactContext.Provider value={{ contacts: state.contacts, addContact }}>
      {props.children}
    </ContactContext.Provider>
  );
};

export default ContactState;
